import React, { useState } from 'react'

const tabsData = [
  {
    label: 'Profile',
    content:
      'User Information',
  },
  {
    label: 'Settings',
    content:
      'General & Privacy Settings',
  },
];

const Account = () => {
  const [activeTabIndex, setActiveTabIndex] = useState(0);
  return (
    <>
        <section>
            <div className="h-screen bg-gradient-to-r from-slate-500 to-yellow-100">
              <p className="text-left p-10 text-2xl font-semibold">Account</p>
              <div className='bg-gradient-to-r from-gray-700 via-gray-900 to-black rounded-lg m-5 text-gray-300 font-semibold text-md'>
                <div>
                  <div className="flex space-x-3 border-b ml-10">
                    {tabsData.map((tab, idx) => {
                      return (
                        <button
                          key={idx}
                          className={`py-2 border-b-4 mr-10 transition-colors duration-300 ${
                            idx === activeTabIndex
                              ? 'border-teal-500 m-2'
                              : 'border-transparent hover:border-gray-200'
                          }`}
                          onClick={() => setActiveTabIndex(idx)}
                        >
                          {tab.label}
                        </button>
                      );
                    })}
                    </div>
                      <div className="py-4 ml-10">
                        <p>{tabsData[activeTabIndex].content}</p>
                      </div>
                </div>
              </div>
            </div>
        </section>
    </>
  )
}

export default Account
