import React from 'react'
import { NavLink } from 'react-router-dom'
import { SidebarData } from '../data/SidebarData'
import { BsArrowLeftShort } from "react-icons/bs";
import { useState } from 'react';

const Sidebar = () => {
    const [open, setOpen] = useState(true);
    const activeLink = "hover:bg-gray-500 bg-gray-500 pl-7 mt-7 w-full h-14 rounded font-bold flex justify-start items-center text-white text-l space-x-1"
    const normalLink = "hover:bg-gray-500 pl-7 mt-7 w-full h-14 rounded font-bold flex justify-start items-center text-white text-l space-x-1"
  return (
    <>
        <section>
            <div className="bg-slate-900 text-xl font-normal relative">
                <BsArrowLeftShort className="text-slate-900 bg-white rounded-full border border-slate-900 cursor-pointer text-xl absolute -right-3 -top-9"/>
                {
                    SidebarData.map((item, index) => {
                        return(
                            <div key={index}>
                                <NavLink to={item.path} 
                                    className={({ isActive }) => 
                                    isActive ? activeLink : normalLink}
                                >
                                    <span>{item.icon}</span>
                                    <span className="pl-3">{item.title}</span>
                                </NavLink>
                            </div>
                        )
                    })
                }
            </div>
        </section>
    </>
  )
}

export default Sidebar
