import React, { useState } from 'react'
import user from "../assets/profile.jpeg"

const tabsData = [
  {
    label: 'My details',
    content:
      'User Information',
  },
  {
    label: 'Settings',
    content:
      'General & Privacy Settings',
  },
  {
    label: 'Password',
    content:
      'Password Settings',
  },
];

const Account = () => {
  const [activeTabIndex, setActiveTabIndex] = useState(0);
  return (
    <>
        <section>
            <div className="h-screen bg-gradient-to-r from-slate-500 to-yellow-100">
              <p className="text-left p-10 text-2xl font-semibold">Partner Profile</p>
              <div className='bg-gradient-to-r from-gray-700 via-gray-900 to-black rounded-lg m-5 text-gray-300 font-semibold text-md'>
                <div>
                  <div className="flex space-x-24 p-3 border-b">
                    {tabsData.map((tab, idx) => {
                      return (
                        <button
                          key={idx}
                          className={`py-2 ml-10 border-b-4 transition-colors duration-300 ${
                            idx === activeTabIndex
                              ? 'border-teal-500'
                              : 'border-transparent hover:border-gray-200'
                          }`}
                          onClick={() => setActiveTabIndex(idx)}
                        >
                          {tab.label}
                        </button>
                      );
                    })}
                    </div>
                      <div className="py-10 ml-10">
                        <p>{tabsData[activeTabIndex].content}</p>
                        <div className="pt-8">
                          <div className="flex flex-col justify-left max-w-xs p-3 rounded-xl sm:px-12 dark:text-gray-100">
                            <img src={user} alt="" className="w-24 h-24 mx-auto rounded-full dark:bg-gray-500 object-cover" />
                            <div className="space-y-4 text-center divide-y divide-gray-700">
                              <div className="my-2 space-y-1">
                                <h2 className="text-xl font-semibold sm:text-2xl">Ritesh Raj</h2>
                                <p className="px-5 text-xs sm:text-base dark:text-gray-400">Freelancer</p>
                              </div>

                            </div>
                            test
                          </div>

                        </div>
                      </div>
                </div>
              </div>
            </div>
        </section>
    </>
  )
}

export default Account
